<div class="create-group" style="display:none;">
    <h4>Create Group</h4>
    <div class="form-group">
        <input type="text" name="group_name" placeholder="Enter group name" class="form-control">
    </div>
    <div class="form-group">
        <textarea name="group_description" placeholder="Enter group description" class="form-control"></textarea>
    </div>
    <div class="form-group">
        <input type="file" name="group_avatar" class="form-control">
    </div>
    <div class="form-group">
        <button type="submit" class="btn btn-primary btn-block">Create</button>
    </div>
</div>
